import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

export async function test_api_auth_user_join(connection: api.IConnection) {
  // Establish temporary session context for the TodoList application
  // The join operation creates a temporary token-based context without user authentication
  // This enables subsequent task operations under implicit user context
  const response: ITodoListUser.IAuthorized =
    await api.functional.auth.user.join(connection);

  // Validate response structure and type safety
  // typia.assert() performs COMPLETE AND PERFECT validation of ALL response properties
  // This includes: id format (UUID), token structure, expired_at and refreshable_until format (ISO 8601)
  typia.assert(response);
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IPageITodoListTodoListTask } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageITodoListTodoListTask";
import type { ITodoListTodoListTask } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListTodoListTask";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

export async function test_api_todo_index_retrieval(
  connection: api.IConnection,
) {
  // Step 1: Establish session context by joining
  const authResponse: ITodoListUser.IAuthorized =
    await api.functional.auth.user.join(connection);
  typia.assert(authResponse);

  // Step 2: Create a single task with test data
  const taskTitle = RandomGenerator.paragraph({
    sentences: 5,
    wordMin: 3,
    wordMax: 8,
  });
  const createdTask: ITodoListTodoListTask =
    await api.functional.todoList.todos.create(connection, {
      body: {
        title: taskTitle,
      } satisfies ITodoListTodoListTask.ICreate,
    });
  typia.assert(createdTask);

  // Step 3: Retrieve all tasks to verify persistence and correct structure
  const taskList: IPageITodoListTodoListTask =
    await api.functional.todoList.todos.index(connection);
  typia.assert(taskList);

  // Validate task count
  TestValidator.equals("task count matches", taskList.data.length, 1);
  TestValidator.equals("task id matches", taskList.data[0].id, createdTask.id);
  TestValidator.equals(
    "task title matches",
    taskList.data[0].title,
    createdTask.title,
  );
  TestValidator.equals(
    "task completion status matches",
    taskList.data[0].is_completed,
    createdTask.is_completed,
  );

  // Validate field type integrity
  const task = taskList.data[0];
  TestValidator.predicate(
    "id is valid UUID format",
    /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(
      task.id,
    ),
  );
  TestValidator.predicate("title is non-empty", task.title.length > 0);
  TestValidator.predicate(
    "title is within length limit",
    task.title.length <= 500,
  );
  TestValidator.predicate(
    "is_completed is boolean",
    typeof task.is_completed === "boolean",
  );

  // Validate pagination structure
  TestValidator.predicate(
    "current page is at least 1",
    taskList.pagination.current >= 1,
  );
  TestValidator.predicate(
    "limit is at least 1",
    taskList.pagination.limit >= 1,
  );
  TestValidator.predicate(
    "records is positive",
    taskList.pagination.records >= 0,
  );
  TestValidator.predicate(
    "pages is at least 1",
    taskList.pagination.pages >= 1,
  );

  // Validate data persistence and ordering
  TestValidator.equals(
    "task data is consistent",
    createdTask.title,
    task.title,
  );
  TestValidator.equals(
    "task completion status is preserved",
    createdTask.is_completed,
    task.is_completed,
  );
  TestValidator.equals("task id is correct", createdTask.id, task.id);

  // Ensure complies with creation order
  // Based on requirement: "returns all previously created tasks in creation order"
  // Since we only created one task, we've validated the entire list
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITodoListTodoListTask } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListTodoListTask";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

export async function test_api_todo_task_creation_with_valid_title(
  connection: api.IConnection,
) {
  // Step 1: Establish session context by joining
  const user: ITodoListUser.IAuthorized =
    await api.functional.auth.user.join(connection);
  typia.assert(user);

  // Step 2: Create a todo task with a valid non-empty title
  const title = RandomGenerator.paragraph({
    sentences: 1,
    wordMin: 5,
    wordMax: 20,
  });
  const task: ITodoListTodoListTask =
    await api.functional.todoList.todos.create(connection, {
      body: {
        title,
      } satisfies ITodoListTodoListTask.ICreate,
    });
  typia.assert(task);

  // Step 3: Validate the created task has the expected properties
  TestValidator.equals("task title matches input", task.title, title);
  TestValidator.equals(
    "task should be initially incomplete",
    task.is_completed,
    false,
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITodoListTodoListTask } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListTodoListTask";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

export async function test_api_todo_task_creation_with_whitespace_only_title(
  connection: api.IConnection,
) {
  // Step 1: Establish temporary session context for the single-user TodoList application
  const session: ITodoListUser.IAuthorized =
    await api.functional.auth.user.join(connection);
  typia.assert(session);

  // Step 2: Test the creation of a todo task with whitespace-only title
  // The system must reject this request and not create any task
  await TestValidator.error(
    "creation of todo with whitespace-only title should fail",
    async () => {
      await api.functional.todoList.todos.create(connection, {
        body: {
          title: "   ", // Pure whitespace - should be rejected by business rule
        } satisfies ITodoListTodoListTask.ICreate,
      });
    },
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITodoListTodoListTask } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListTodoListTask";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

export async function test_api_todo_task_completion_toggle_success(
  connection: api.IConnection,
) {
  // Step 1: Establish session context by joining the TodoList application
  const session: ITodoListUser.IAuthorized =
    await api.functional.auth.user.join(connection);
  typia.assert(session);

  // Step 2: Create a new task with a random title
  const taskTitle = RandomGenerator.paragraph({
    sentences: 3,
    wordMin: 3,
    wordMax: 8,
  });
  const createdTask: ITodoListTodoListTask =
    await api.functional.todoList.todos.create(connection, {
      body: {
        title: taskTitle,
      } satisfies ITodoListTodoListTask.ICreate,
    });
  typia.assert(createdTask);
  TestValidator.equals(
    "task initially incomplete",
    createdTask.is_completed,
    false,
  );

  // Step 3: Toggle the task's completion status to true
  const toggledToTrue: ITodoListTodoListTask =
    await api.functional.todoList.todos.update(connection, {
      id: createdTask.id,
      body: {
        is_completed: true,
      } satisfies ITodoListTodoListTask.IUpdate,
    });
  typia.assert(toggledToTrue);
  TestValidator.equals(
    "task toggled to complete",
    toggledToTrue.is_completed,
    true,
  );

  // Step 4: Toggle the task's completion status back to false
  const toggledToFalse: ITodoListTodoListTask =
    await api.functional.todoList.todos.update(connection, {
      id: createdTask.id,
      body: {
        is_completed: false,
      } satisfies ITodoListTodoListTask.IUpdate,
    });
  typia.assert(toggledToFalse);
  TestValidator.equals(
    "task toggled back to incomplete",
    toggledToFalse.is_completed,
    false,
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITodoListTodoListTask } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListTodoListTask";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

export async function test_api_todo_task_deletion_success(
  connection: api.IConnection,
) {
  // Step 1: Establish session context by joining authentication
  const session: ITodoListUser.IAuthorized =
    await api.functional.auth.user.join(connection);
  typia.assert(session);

  // Step 2: Create a new todo task
  const createResponse: ITodoListTodoListTask =
    await api.functional.todoList.todos.create(connection, {
      body: {
        title: RandomGenerator.paragraph({
          sentences: 3,
          wordMin: 4,
          wordMax: 8,
        }),
      } satisfies ITodoListTodoListTask.ICreate,
    });
  typia.assert(createResponse);

  // Step 3: Delete the created task
  // The API's documentation states deletion silently ignores non-existent IDs,
  // meaning if the deletion fails to find the ID, it still succeeds without error.
  // Since there's no way to verify the task was deleted (no GET endpoint provided),
  // we rely on the successful operation completion as verification of functionality.
  await api.functional.todoList.todos.erase(connection, {
    id: createResponse.id,
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

export async function test_api_todo_task_deletion_on_non_existent_id(
  connection: api.IConnection,
) {
  // First, establish the temporary session context required for all operations
  const authResponse: ITodoListUser.IAuthorized =
    await api.functional.auth.user.join(connection);
  typia.assert(authResponse);

  // Verify that the Authorization header was properly set by the join operation
  // This ensures the connection has the correct authentication token
  TestValidator.predicate(
    "Authorization header was set after join",
    connection.headers?.Authorization !== undefined &&
      connection.headers?.Authorization !== null &&
      connection.headers?.Authorization !== "",
  );

  // Generate a non-existent UUID for deletion test
  const nonExistentId: string = typia.random<string & tags.Format<"uuid">>();

  // Test that deleting a non-existent ID silently succeeds (returns 204 No Content)
  // The API should not throw an error and should return no response body
  // We verify this by ensuring no HTTP error is thrown during the operation
  await TestValidator.error(
    "deletion of non-existent ID should not throw HTTP error",
    async () => {
      await api.functional.todoList.todos.erase(connection, {
        id: nonExistentId,
      });
    },
  );

  // The test is complete - the combination of successful authentication and
  // successful silent deletion confirms the expected behavior
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

export async function test_api_user_login_existing(
  connection: api.IConnection,
) {
  // Step 1: Create new user context with join operation
  const joinResponse: ITodoListUser.IAuthorized =
    await api.functional.auth.user.join(connection);
  typia.assert(joinResponse);

  // Store the original join token for comparison
  const originalToken = joinResponse.token.access;

  // Step 2: Call login endpoint to obtain new token
  const loginResponse: ITodoListUser.IAuthorized =
    await api.functional.auth.user.login(connection);
  typia.assert(loginResponse);

  // Step 3: Validate that login issued a new token different from join token
  TestValidator.notEquals(
    "login token must be different from join token",
    loginResponse.token.access,
    originalToken,
  );

  // Step 4: Validate that the session context is maintained (connection headers updated)
  TestValidator.equals(
    "connection header Authorization must be updated with new token",
    connection.headers?.Authorization,
    loginResponse.token.access,
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAuthorizationToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IAuthorizationToken";
import type { ITodoListUser } from "@ORGANIZATION/PROJECT-api/lib/structures/ITodoListUser";

export async function test_api_user_refresh_token(connection: api.IConnection) {
  // Step 1: Create initial user session context by joining
  const initialSession: ITodoListUser.IAuthorized =
    await api.functional.auth.user.join(connection);
  typia.assert(initialSession);

  // Step 2: Store the initial token for comparison
  const initialAccessToken = initialSession.token.access;
  const initialRefreshToken = initialSession.token.refresh;
  const initialExpiredAt = initialSession.token.expired_at;
  const initialRefreshableUntil = initialSession.token.refreshable_until;

  // Step 3: Invoke the refresh endpoint to renew the token
  const refreshedSession: ITodoListUser.IAuthorized =
    await api.functional.auth.user.refresh(connection);
  typia.assert(refreshedSession);

  // Step 4: Validate that the new token is different from the original
  TestValidator.notEquals(
    "refreshed access token should be different from initial access token",
    refreshedSession.token.access,
    initialAccessToken,
  );

  TestValidator.notEquals(
    "refreshed refresh token should be different from initial refresh token",
    refreshedSession.token.refresh,
    initialRefreshToken,
  );

  // Step 5: Validate that the expiration time is updated
  TestValidator.notEquals(
    "refreshed expired_at should be different from initial expired_at",
    refreshedSession.token.expired_at,
    initialExpiredAt,
  );

  TestValidator.notEquals(
    "refreshed refreshable_until should be different from initial refreshable_until",
    refreshedSession.token.refreshable_until,
    initialRefreshableUntil,
  );

  // Step 6: Validate that the new session maintains access to data (indirect validation)
  // Since the system has no user-specific data stored, we verify the refreshed token works
  // by attempting to use it again for another refresh (though the token is automatically handled)
  const secondRefreshedSession: ITodoListUser.IAuthorized =
    await api.functional.auth.user.refresh(connection);
  typia.assert(secondRefreshedSession);

  // Step 7: Confirm that the refreshed token continues to work
  TestValidator.notEquals(
    "second refresh access token should be different from first refresh token",
    secondRefreshedSession.token.access,
    refreshedSession.token.access,
  );

  // Step 8: Verify that unauthenticated refresh attempts fail
  // The refresh endpoint does not require authentication, so we cannot test 401 status as per the schema
  // The endpoint is designed to work without authentication, so this requirement is irrelevant
  // No additional test needed as the functionality is designed to work without authentication
}

